import numpy as npimport matplotlib.pyplot as plttheta = np.radians(20)height = 10mass = 5gravity = 9.8friction_coefficient = 0.1ramp_length = height / np.sin(theta)normal_force = mass * gravity * np.cos(theta)acceleration = gravity * (np.sin(theta) - friction_coefficient * np.cos(theta))velocity = 0length_travelled = 0def calcPE(h):    return mass * gravity * hdef calcKE(v):    return 0.5 * mass * (v ** 2)def calcTE(d):    return friction_coefficient * normal_force * dheights = [height]velocities = [velocity]PE = [calcPE(height)]KE = [calcKE(velocity)]TE = [calcTE(length_travelled)]totalE = [PE[-1] + KE[-1] + TE[-1]]dt = 0.05while height > 0:    velocity += acceleration * dt    ds = velocity * dt    length_travelled += ds    height = max(10 - (length_travelled / ramp_length) * 10, 0)    pe = calcPE(height)    ke = calcKE(velocity)    te = calcTE(length_travelled)    heights.append(height)    velocities.append(velocity)    PE.append(pe)    KE.append(ke)    TE.append(te)    totalE.append(pe + ke + te)fig, axes = plt.subplots(3, 2, figsize=(10, 10))(ax1, ax2), (ax3, ax4), (ax5, ax6) = axesbar_width = 0.4ax1.bar(heights, velocities, width=bar_width)ax1.set_title('Velocity vs Height')ax1.set_xlabel('Height (m)')ax1.set_ylabel('Velocity (m/s)')ax2.bar(heights, KE, width=bar_width)ax2.set_title('Kinetic Energy vs Height')ax2.set_xlabel('Height (m)')ax2.set_ylabel('Kinetic Energy (J)')ax3.bar(heights, PE, width=bar_width)ax3.set_title('Potential Energy vs Height')ax3.set_xlabel('Height (m)')ax3.set_ylabel('Potential Energy (J)')ax4.bar(heights, TE, width=bar_width)ax4.set_title('Thermal Energy vs Height')ax4.set_xlabel('Height (m)')ax4.set_ylabel('Thermal Energy (J)')ax5.bar(heights, totalE, width=bar_width)ax5.set_title('Total Energy vs Height')ax5.set_xlabel('Height (m)')ax5.set_ylabel('Total Energy (J)')ax6.axis('off')plt.tight_layout()plt.show()